using System;\nusing NLog;\nusing NLog.Targets;\n\n[Target("LinqToSqlTarget")]\npublic class LinqToSqlTarget : Target\n{\n    protected override void Write(LogEventInfo logEvent)\n    {\n        var dataContext = new YourDataContext(); // Replace with your actual DataContext\n\n        // Create a log object and set the properties from the log event\n        var logEntry = new Log // Replace with your actual Log table class\n        {\n            Level = logEvent.Level.Name,\n            Message = logEvent.Message,\n            TimeStamp = DateTime.Now, // You could use logEvent.TimeStamp if you prefer\n            Exception = logEvent.Exception?.ToString()\n        };\n\n        dataContext.Logs.InsertOnSubmit(logEntry); // Assuming 'Logs' is your table name in DataContext\n        dataContext.SubmitChanges();\n    }\n}