using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    [DllImport("advapi32.dll", EntryPoint = "RegSetValueExW")]\n    public static extern int RegSetValueExW(\n        uint hKey,\n        string lpValueName,\n        uint lpReserved,\n        uint lpType,\n        byte[] lpData,\n        uint lpcbData);\n\n    const uint HKEY_LOCAL_MACHINE = 0x80000002;\n    const uint REG_DWORD = 4;\n\n    static void Main()\n    {\n        string keyPath = "Software\\MyApp";\n        using (Microsoft.Win32.RegistryKey key = Microsoft.Win32.Registry.LocalMachine.OpenSubKey(keyPath, true))\n        {\n            if (key == null)\n            {\n                Console.WriteLine("Registry path does not exist.");\n                return;\n            }\n\n            int valueToSet = 5;\n            byte[] bytes = BitConverter.GetBytes(valueToSet);\n\n            // Convert the integer to a byte array\n            int result = RegSetValueExW((uint)key.Handle, "MyDWORDValue", 0, REG_DWORD, bytes, (uint)bytes.Length);\n            if (result == 0)\n            {\n                Console.WriteLine("Registry value set successfully.");\n            }\n            else\n            {\n                Console.WriteLine($"Failed to set registry value. Error code: {result}");\n            }\n        }\n    }\n}