using System;\nusing System.Management;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string remoteComputerName = "RemoteMachineName"; // Replace with your remote machine name\n        string query = "SELECT * FROM Win32_Processor";\n\n        using (ManagementObjectSearcher searcher =\n            new ManagementObjectSearcher($"\\\\{remoteComputerName}\\root\\cimv2", query))\n        {\n            foreach (var m in searcher.Get())\n            {\n                uint loadPercentage = (uint)m["LoadPercentage"];\n                Console.WriteLine($"CPU Load on {remoteComputerName}: {loadPercentage}%");\n            }\n        }\n    }\n}