using System;\nusing System.Collections.Generic;\nusing System.Web.Services;\n\n[WebService(Namespace = "http://yournamespace.com/")]\npublic class YourWebService : WebService\n{\n    private static List<string> AllowedIPs = new List<string>\n    {\n        "192.168.1.1", // Add your allowed IPs here\n        "10.0.0.1"\n    };\n\n    [WebMethod]\n    public string YourWebServiceMethod(string param)\n    {\n        if (!IsAllowedIpAddress(Context))\n        {\n            throw new Exception("Access denied");\n        }\n\n        // Your method logic here\n        return "Success";\n    }\n\n    private bool IsAllowedIpAddress(HttpContext context)\n    {\n        string ipAddress = context.Request.ServerVariables["REMOTE_ADDR"];\n        return AllowedIPs.Contains(ipAddress);\n    }\n}