using System;\nusing System.Xml;\nusing HtmlAgilityPack;\n\npublic class DOOMCalculator\n{\n    public static void Main()\n    {\n        string xmlContent = @"<YourXMLHere>...</YourXMLHere>"; // Your XML containing HTML tags\n\n        XmlDocument xmlDoc = new XmlDocument();\n        xmlDoc.LoadXml(xmlContent);\n\n        foreach (XmlNode node in xmlDoc.DocumentElement.SelectNodes("//nodeWithHTML")) // replace with your actual XPath\n        {\n            HtmlDocument htmlDoc = new HtmlDocument();\n            htmlDoc.LoadHtml(node.InnerText); // Load the text content which contains HTML\n\n            // Select and remove all div elements\n            var nodesToRemove = htmlDoc.DocumentNode.SelectNodes("//div");\n            if (nodesToRemove != null)\n            {\n                foreach (var nodeToRemove in nodesToRemove)\n                    nodeToRemove.Remove();\n            }\n\n            // Update the original XML node with cleaned text\n            node.InnerText = HtmlEntity.DeEntitize(htmlDoc.DocumentNode.OuterHtml).Trim();\n        }\n\n        Console.WriteLine(xmlDoc.OuterXml); // Output the cleaned XML\n    }\n}