using Microsoft.AspNetCore.Mvc;\nusing YourNamespace.YourLibrary;\n\nnamespace YourApi.Controllers\n{\n    [ApiController]\n    [Route("api/[controller]")]\n    public class DataController : ControllerBase\n    {\n        private readonly IYourLibraryService _yourLibraryService;\n\n        public DataController(IYourLibraryService yourLibraryService)\n        {\n            _yourLibraryService = yourLibraryService;\n        }\n\n        [HttpGet("{id}")]\n        public IActionResult GetData(int id)\n        {\n            var data = _yourLibraryService.GetDataById(id);\n            if (data == null)\n            {\n                return NotFound();\n            }\n            return Ok(data);\n        }\n    }\n}