using System;\nusing System.Management;\n\nclass Program\n{\n    static void Main()\n    {\n        string siteName = "Default Web Site";\n        int? siteId = GetSiteId(siteName);\n\n        if (siteId.HasValue)\n        {\n            Console.WriteLine($"The ID for the site '{siteName}' is {siteId.Value}.");\n        }\n        else\n        {\n            Console.WriteLine($"No site found with the name '{siteName}'.");\n        }\n    }\n\n    static int? GetSiteId(string siteName)\n    {\n        string query = $"SELECT * FROM IIsWebServerSetting WHERE ServerComment='{siteName}'";\n        \n        using (ManagementObjectSearcher searcher = new ManagementObjectSearcher(query))\n        {\n            foreach (ManagementObject mo in searcher.Get())\n            {\n                return int.Parse(mo["Name"].ToString());\n            }\n        }\n\n        return null;\n    }\n}