using (OleDbConnection connection = new OleDbConnection("yourConnectionString"))\n{\n    try\n    {\n        string query = "UPDATE tableName SET LastPolledDtg = @LastPolledDtg WHERE [key] = @ID;";\n        OleDbCommand command = new OleDbCommand(query, connection);\n        \n        command.Parameters.AddWithValue("@ID", id);\n        command.Parameters.AddWithValue("@LastPolledDtg", DateTime.Now);\n\n        connection.Open();\n        int affectedRows = command.ExecuteNonQuery();\n\n        Console.WriteLine($"{affectedRows} rows updated.");\n    }\n    catch (Exception ex)\n    {\n        Console.WriteLine($"An error occurred: {ex.Message}");\n    }\n}