private void compose()\n{\n    try {\n        var catalog = new DirectoryCatalog("controllers");\n        if (catalog.Parts.Count() == 0) \n            Console.WriteLine("No parts found in 'controllers' directory.");\n\n        var container = new CompositionContainer(catalog);\n\n        container.AddPart(this);\n        bool success = container.Compose();\n\n        if (!success)\n            Console.WriteLine("Composition failed, no exports were found and thus imports could not be satisfied.");\n    } catch (Exception ex) {\n        Console.WriteLine($"Error during composition: {ex.Message}");\n    }\n}