using System;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        string xmlString = @"<root>\n                                <child>\n                                    <anotherChild>\n                                       <key>1</key>\n                                    </anotherChild>\n                                    <leaf1>X</leaf1>\n                                    <leaf2>Y</leaf2>\n                                    <leaf3></leaf3>\n                                </child>\n                                <child>\n                                    <anotherChild>\n                                       <key>2</key>\n                                    </anotherChild>\n                                    <leaf1>A</leaf1>\n                                    <leaf2>B</leaf2>\n                                    <leaf3></leaf3>\n                                </child>\n                            </root>";\n\n        XmlDocument xmlDoc = new XmlDocument();\n        xmlDoc.LoadXml(xmlString);\n\n        // XPath to find leaf2 where key == 2\n        string xpathQuery = @"//child[anotherChild/key='2']/leaf2";\n        \n        XmlNode node = xmlDoc.SelectSingleNode(xpathQuery);\n        \n        if (node != null)\n        {\n            Console.WriteLine("Value of leaf2: " + node.InnerText); // Output should be 'B'\n        }\n        else\n        {\n            Console.WriteLine("Node not found.");\n        }\n    }\n}