using System;\nusing System.IO;\n\npublic static void CopyDirectory(string sourceDir, string destinationDir)\n{\n    // Check if source exists\n    if (!Directory.Exists(sourceDir))\n        throw new DirectoryNotFoundException($"Source directory not found: {sourceDir}");\n\n    // Get information about the source directory\n    var dir = new DirectoryInfo(sourceDir);\n    \n    // Create or confirm the destination directory exists.\n    if (!dir.Exists)\n        return;\n\n    if (!Directory.Exists(destinationDir))\n        Directory.CreateDirectory(destinationDir);\n\n    // Get the files in the source directory and copy to the destination directory.\n    foreach (var fileInfo in dir.GetFiles())\n    {\n        var targetFilePath = Path.Combine(destinationDir, fileInfo.Name);\n        fileInfo.CopyTo(targetFilePath);\n    }\n\n    // Copy each subdirectory using recursion.\n    foreach (DirectoryInfo subdir in dir.GetDirectories())\n    {\n        var targetSubdirPath = Path.Combine(destinationDir, subdir.Name);\n        CopyDirectory(subdir.FullName, targetSubdirPath);\n    }\n}