using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        List<List<uint>> listOfLists = new List<List<uint>>\n        {\n            new List<uint> { 32, 1, 1, 1, 1, 1 },\n            new List<uint> { 22, 2, 2, 2, 2, 2 },\n            new List<uint> { 32, 1, 1, 1, 1, 1 },\n            new List<uint> { 1, 1, 1, 1, 1 }\n        };\n\n        // Sort in ascending order\n        var sortedListAsc = listOfLists.OrderBy(innerList => innerList[0]).ToList();\n\n        // Print the sorted list\n        foreach (var innerList in sortedListAsc)\n        {\n            Console.WriteLine(string.Join(", ", innerList));\n        }\n\n        // To sort in descending order, simply use OrderByDescending instead of OrderBy\n        var sortedListDesc = listOfLists.OrderByDescending(innerList => innerList[0]).ToList();\n\n        // Print the sorted list in descending order\n        Console.WriteLine("\nSorted in Descending Order:");\n        foreach (var innerList in sortedListDesc)\n        {\n            Console.WriteLine(string.Join(", ", innerList));\n        }\n    }\n}