// Using Select & SelectMany (assuming Orders belong to Customers)\nvar customerOrders = from c in db.Customers\n                     select new {\n                         CustomerName = c.Name,\n                         Orders = c.Orders.Select(o => o.OrderDate).ToList()\n                     };\n\n// Using Join (more explicit and sometimes easier to visualize joins)\nvar result = from c in db.Customers\n             join o in db.Orders on c.CustomerID equals o.CustomerID\n             select new {\n                 CustomerName = c.Name,\n                 OrderDate = o.OrderDate\n             };