using System;\nusing System.Diagnostics.Tracing;\n\npublic class ServiceMonitor : EventSource {\n    public static readonly ServiceMonitor Log = new ServiceMonitor();\n\n    [Event(1, Message = "Items Processed: {0}", Level = EventLevel.Informational)]\n    public void ItemsProcessed(long count) => WriteEvent(1, count);\n\n    [Event(2, Message = "Last Item Process Time: {0}", Level = EventLevel.Informational)]\n    public void LastItemProcessTime(DateTime time) => WriteEvent(2, time.ToString());\n}\n\n// Usage\nServiceMonitor.Log.ItemsProcessed(5);\nServiceMonitor.Log.LastItemProcessTime(DateTime.Now);