using System;\nusing System.IO;\nusing Oracle.ManagedDataAccess.Client;\n\nnamespace DataLoadService\n{\n    class BulkLoader\n    {\n        static void Main(string[] args)\n        {\n            string connectionString = "your_connection_string_here";\n            string dataFilePath = "path_to_your_data_file.txt";\n\n            using (OracleConnection connection = new OracleConnection(connectionString))\n            {\n                connection.Open();\n\n                // Prepare the OracleBulkCopy object\n                using (OracleBulkCopy bulkCopy = new OracleBulkCopy(connection))\n                {\n                    bulkCopy.DestinationTableName = "YOUR_TARGET_TABLE";\n                    \n                    // Map columns if necessary, assuming the file and table structure align\n                    bulkCopy.ColumnMappings.Add(0, "COLUMN1");\n                    bulkCopy.ColumnMappings.Add(1, "COLUMN2");\n                    bulkCopy.ColumnMappings.Add(2, "COLUMN3");\n\n                    // Optionally configure batch size and notify after rows inserted\n                    bulkCopy.BatchSize = 500;\n                    bulkCopy.NotifyAfter = 1000;\n                    bulkCopy.SqlRowsCopied += new OracleRowsCopiedEventHandler(bulkCopy_SqlRowsCopied);\n\n                    // Load data\n                    using (StreamReader reader = new StreamReader(dataFilePath))\n                    {\n                        bulkCopy.WriteToServer(reader);\n                    }\n                }\n            }\n        }\n\n        static void bulkCopy_SqlRowsCopied(object sender, OracleRowsCopiedEventArgs e)\n        {\n            Console.WriteLine($"Rows copied: {e.RowsCopied}");\n        }\n    }\n}