using System;\nusing System.Collections.Generic;\nusing System.IO;\nusing iTextSharp.text.pdf;\nusing iTextSharp.text.pdf.parser;\n\nclass Program\n{\n    static void Main()\n    {\n        string pdfPath = "yourfile.pdf";\n        string searchString = "filename.txt";\n        \n        List<string> matchedLines = ExtractAndSearch(pdfPath, searchString);\n        \n        foreach (var line in matchedLines)\n        {\n            Console.WriteLine(line);\n        }\n    }\n\n    private static List<string> ExtractAndSearch(string path, string searchTerm)\n    {\n        using PdfReader reader = new PdfReader(path);\n        var matchedLines = new List<string>();\n\n        for (int i = 1; i <= reader.NumberOfPages; i++)\n        {\n            // Extract text from each page\n            string pageText = PdfTextExtractor.GetTextFromPage(reader, i);\n\n            // Split the extracted text into lines\n            string[] lines = pageText.Split(new[] { "\r\n", "\r", "\n" }, StringSplitOptions.RemoveEmptyEntries);\n\n            foreach (var line in lines)\n            {\n                if (line.Contains(searchTerm))\n                {\n                    matchedLines.Add(line);\n                }\n            }\n        }\n\n        return matchedLines;\n    }\n}