using System;\nusing System.Data.SqlClient;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "your_connection_string_here";\n        string filePath = @"C:\path\to\your\file.pdf";\n\n        using (var connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n\n            // Read the file into a byte array.\n            byte[] pdfData = File.ReadAllBytes(filePath);\n\n            // Insert the data into the database.\n            string sql = "INSERT INTO YourTable (PdfColumn, FileName) VALUES (@PDFData, @FileName)";\n            using (var command = new SqlCommand(sql, connection))\n            {\n                command.Parameters.AddWithValue("@PDFData", pdfData);\n                command.Parameters.AddWithValue("@FileName", Path.GetFileName(filePath));\n                command.ExecuteNonQuery();\n            }\n        }\n    }\n}