protected void grdObitList_RowDataBound(object sender, GridViewRowEventArgs e)\n{\n    if (e.Row.RowType == DataControlRowType.DataRow)\n    {\n        // Find the HyperLinkField control within the row.\n        HyperLink hypName = e.Row.FindControl("HyperLinkColumn_Name") as HyperLink;\n        if (hypName != null)\n        {\n            int currentPageIndex = grdObitList.PageIndex + 1; // PageIndex is zero-based\n            string currentHref = hypName.NavigateUrl;\n\n            // If the URL already contains a query string, append "&" instead of "?"\n            int indexQueryChar = currentHref.IndexOf('?');\n            char prefix = (indexQueryChar >= 0) ? '&' : '?';\n\n            // Append the page number to the hyperlink's NavigateURL\n            hypName.NavigateUrl = $"{currentHref}{prefix}page={currentPageIndex}";\n        }\n    }\n}