public class WikiController : Controller\n{\n    public ActionResult Page(string path)\n    {\n        // Process the path here\n        // For example, split the path into segments and use them as needed.\n        var segments = path.Split('/');\n        ViewBag.Message = $"Path: {string.Join("/", segments)}"; // Display the path for demonstration purposes.\n        \n        return View();\n    }\n}