using System.ServiceModel;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        Uri baseAddress = new Uri("net.tcp://localhost:8080/myservice");\n\n        using (ServiceHost serviceHost = new ServiceHost(typeof(MyRpcService), baseAddress))\n        {\n            NetTcpBinding binding = new NetTcpBinding();\n            serviceHost.AddServiceEndpoint(typeof(IMyRpcService), binding, "MyOperation");\n            \n            Console.WriteLine("The service is ready.");\n            Console.WriteLine("Press <enter> to stop the service.");\n            Console.ReadLine();\n\n            serviceHost.Close();\n        }\n    }\n}