using System.ServiceModel;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        NetTcpBinding binding = new NetTcpBinding();\n        var endpoint = new EndpointAddress("net.tcp://localhost:8080/myservice/MyOperation");\n\n        using (ChannelFactory<IMyRpcService> channelFactory = new ChannelFactory<IMyRpcService>(binding, endpoint))\n        {\n            IMyRpcService client = channelFactory.CreateChannel();\n\n            Console.WriteLine(client.MyOperation("Hello, WCF!"));\n        }\n    }\n}