using System;\nusing System.Data; // For DataSet, DataTable, etc.\nusing System.Drawing; // For Bitmap, Image\nusing System.IO; // For MemoryStream\n\npublic Bitmap ConvertJpegBlobToBitmap(byte[] jpegBytes)\n{\n    if (jpegBytes == null || jpegBytes.Length == 0)\n        throw new ArgumentException("JPEG bytes cannot be null or empty.", nameof(jpegBytes));\n\n    using (var memoryStream = new MemoryStream(jpegBytes))\n    {\n        return (Bitmap)Image.FromStream(memoryStream);\n    }\n}\n\n// Example usage:\npublic void ExampleUsage(byte[] jpegBlobFromDatabase)\n{\n    try\n    {\n        Bitmap bitmap = ConvertJpegBlobToBitmap(jpegBlobFromDatabase);\n        \n        // Now you can pass 'bitmap' to your third-party library.\n        // Remember, you should handle the Bitmap disposal properly.\n        bitmap.Dispose();\n    }\n    catch (Exception ex)\n    {\n        Console.WriteLine($"An error occurred: {ex.Message}");\n    }\n}