using (var connection = new SqlConnection(connectionString))\n{\n    connection.Open();\n\n    // Retrieve the data from the database.\n    string sql = "SELECT PdfColumn, FileName FROM YourTable WHERE SomeCondition";\n    using (var command = new SqlCommand(sql, connection))\n    {\n        using (var reader = command.ExecuteReader())\n        {\n            if (reader.Read())\n            {\n                byte[] pdfData = (byte[])reader["PdfColumn"];\n                string fileName = reader["FileName"] as string;\n\n                // Save the file to disk.\n                File.WriteAllBytes(Path.Combine(@"C:\path\to\save\", fileName), pdfData);\n\n                // Alternatively, you can stream the data directly to a user in a web application context.\n            }\n        }\n    }\n}