// Configuring MassTransit in ASP.NET Core\nusing MassTransit;\nusing Microsoft.Extensions.DependencyInjection;\n\npublic class Startup\n{\n    public void ConfigureServices(IServiceCollection services)\n    {\n        services.AddMassTransit(x =>\n        {\n            x.UsingRabbitMq((context, config) =>\n            {\n                // Use configuration from appsettings.json or similar\n                string host = Configuration.GetConnectionString("MessageQueueHost");\n                config.Host(host);\n                \n                // Define consumer and endpoint mappings\n                config.ConfigureEndpoints(context);\n            });\n        });\n\n        services.AddMassTransitHostedService(); // Adds MassTransit to run as part of the ASP.NET Core hosted service lifecycle\n    }\n}