[HttpPost] // Ensure this endpoint is not accessible via GET from the browser\n   public JsonResult FindWidgets()\n   {\n       if (Request.IsAjaxRequest())\n       {\n           var data = GetWidgetData(); // Assuming you have a method to get your data\n           return Json(data, JsonRequestBehavior.AllowGet);\n       }\n       else\n       {\n           throw new HttpException(400, "Bad request");\n       }\n   }