// Ensure you don't remove the existing ClientCredentials instance\nvar customCredentials = this.ChannelFactory.Endpoint.Behaviors.Find<CentralAuthClientCredentials>();\n\nif (customCredentials != null)\n{\n    // Here you can modify the existing credentials if necessary\n    customCredentials.ClientCertificate.Certificate = ...; // Set your certificate logic here\n    customCredentials.ServiceCertificate.DefaultCertificate = ...; // Set your service certificate logic here\n}\nelse\n{\n    // If for some reason, no CentralAuthClientCredentials exists, create and add them\n    customCredentials = new CentralAuthClientCredentials();\n    customCredentials.ClientCertificate.Certificate = ...; // Set your certificate logic here\n    customCredentials.ServiceCertificate.DefaultCertificate = ...; // Set your service certificate logic here\n\n    this.ChannelFactory.Endpoint.Behaviors.Add(customCredentials);\n}\n\nreturn base.Channel.MyServiceMethod();