using System;\nusing System.Security.Cryptography;\nusing BCrypt.Net;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Register a user\n        string userPassword = "userSecret";\n        string hashedPassword = BCrypt.HashPassword(userPassword);\n\n        // Store hashedPassword in your database\n\n        // Authenticate user\n        bool isAuthenticated = BCrypt.Verify(userPassword, hashedPassword); // Retrieve and compare hashed password from database\n\n        Console.WriteLine(isAuthenticated);\n    }\n}