using System;\nusing System.Web;\n\npublic class QueryStringParser\n{\n    public static NameValueCollection ParseQueryString(string queryString)\n    {\n        if (string.IsNullOrEmpty(queryString))\n            throw new ArgumentException("Query string cannot be null or empty", nameof(queryString));\n\n        return HttpUtility.ParseQueryString(queryString);\n    }\n\n    public static void Main()\n    {\n        string link = "?action=a&current=b&something=c";\n        NameValueCollection q = ParseQueryString(link.Substring(1)); // Remove '?' from the beginning\n\n        string val = q["current"];\n        Console.WriteLine(val); // Output: b\n    }\n}