// Simple C# socket server example\nusing System;\nusing System.Net;\nusing System.Net.Sockets;\nusing System.Text;\n\npublic class TcpServer\n{\n    private const int Port = 5001; // Define your port\n\n    public static void Main()\n    {\n        IPAddress localAddr = IPAddress.Parse("127.0.0.1"); // Adjust as needed\n        TcpListener server = new TcpListener(localAddr, Port);\n        server.Start();\n\n        Console.WriteLine("Waiting for a connection...");\n        TcpClient client = server.AcceptTcpClient();\n        Console.WriteLine("Connected!");\n\n        NetworkStream stream = client.GetStream();\n        byte[] bytes = new byte[1024];\n        int i;\n\n        // Process the data sent by the client.\n        while ((i = stream.Read(bytes, 0, bytes.Length)) != 0)\n        {\n            string dataReceived = Encoding.ASCII.GetString(bytes, 0, i);\n            Console.WriteLine("Received: {0}", dataReceived);\n\n            // Execute executable if command matches\n            if (dataReceived.Trim() == "RunExe")\n            {\n                System.Diagnostics.Process.Start("C:\\Path\\To\\Executable.exe", "/arg1 /arg2");\n            }\n\n            byte[] msg = Encoding.ASCII.GetBytes("Command received.");\n            stream.Write(msg, 0, msg.Length);\n        }\n\n        client.Close();\n        server.Stop();\n    }\n}