protected void Repeater1_ItemDataBound(object sender, RepeaterItemEventArgs e)\n{\n    if (e.Item.ItemType == ListItemType.Item || e.Item.ItemType == ListItemType.AlternatingItem)\n    {\n        var dataRowView = (DataRowView)e.Item.DataItem;\n        bool isDefault = Convert.ToBoolean(dataRowView["IsDefault"]);\n        HtmlInputRadioButton radioButton = (HtmlInputRadioButton)e.Item.FindControl("rbDefaultPhone");\n        \n        if (radioButton != null)\n        {\n            radioButton.Checked = GetIsChecked(isDefault);\n        }\n    }\n}\n\nprotected bool GetIsChecked(bool value)\n{\n    // Your logic here to return true/false\n    return value;\n}