using System;\nusing System.Web;\n\npublic class UserModel\n{\n    // Properties like settings stored here\n    public string Setting1 { get; set; }\n    public int Setting2 { get; set; }\n\n    // Method to save user model to session\n    public void SaveToSession()\n    {\n        HttpContext.Current.Session["UserModel"] = this;\n    }\n\n    // Static method to retrieve the user model from session\n    public static UserModel FromSession()\n    {\n        if (HttpContext.Current.Session["UserModel"] != null)\n        {\n            return (UserModel)HttpContext.Current.Session["UserModel"];\n        }\n        return null; // Or throw an exception, depending on your error handling strategy\n    }\n}