using System;\nusing Microsoft.Win32;\n\nclass Program {\n    static void Main() {\n        // This is a simplified check and won't replace official documentation checks.\n        Console.WriteLine("Checking minimum system requirements...");\n\n        string cpuSpeed = GetCpuInformation().ToString();\n        long ramAmount = GetRamAmount();\n\n        Console.WriteLine($"CPU: ~{cpuSpeed} GHz");\n        Console.WriteLine($"RAM: {ramAmount / 1024 / 1024} MB");\n\n        if (double.Parse(cpuSpeed) >= 1.2 && ramAmount >= 1024 * 1024 * 1024) {\n            Console.WriteLine("System may meet minimum requirements for Visual Studio 2005.");\n        } else {\n            Console.WriteLine("System is likely underpowered for Visual Studio 2005.");\n        }\n    }\n\n    static object GetCpuInformation() {\n        using (ManagementObjectSearcher searcher = new ManagementObjectSearcher("SELECT * FROM Win32_Processor")) {\n            foreach (var mObject in searcher.Get()) {\n                return mObject["CurrentClockSpeed"];\n            }\n        }\n        return null;\n    }\n\n    static long GetRamAmount() {\n        string keyName = "HKEY_LOCAL_MACHINE\\SYSTEM\\CurrentControlSet\\Control\\Session Manager\\Memory Management";\n        object value = Registry.GetValue(keyName, "PhysicalAddressExtension", null);\n        if (value != null) {\n            bool paEx = (int)value == 1;\n            long memSize = new Microsoft.VisualBasic.Devices.ComputerInfo().TotalPhysicalMemory;\n\n            return memSize;\n        }\n        return -1;\n    }\n}