private void OnButtonSubmitClick(object sender, EventArgs e) {\n    List<string> errorMessages = new List<string>();\n\n    if (string.IsNullOrWhiteSpace(textBoxName.Text)) {\n        errorMessages.Add("Name is required.");\n    }\n\n    if (!DateTime.TryParse(textBoxDate.Text, out _)) {\n        errorMessages.Add("Date must be in a valid format.");\n    }\n\n    // Add more validation checks here as needed...\n\n    if (errorMessages.Count > 0) {\n        MessageBox.Show(string.Join("\n", errorMessages), "Validation Errors", MessageBoxButtons.OK, MessageBoxIcon.Warning);\n    } else {\n        MessageBox.Show("Form submitted successfully!", "Success", MessageBoxButtons.OK, MessageBoxIcon.Information);\n    }\n}