using CrystalDecisions.CrystalReports.Engine;\nusing CrystalDecisions.Shared;\n\n// Create an instance of ReportDocument\nReportDocument reportDocument = new ReportDocument();\n\n// Load your crystal report file\nreportDocument.Load(Server.MapPath("YourReport.rpt"));\n\n// Set the database logon information\nTableLogOnInfos crTableLogonInfos = reportDocument.Database.Tables.LogOnInfo;\nConnectionInfo crConnectionInfo = new ConnectionInfo() {\n    ServerName = "UATDBServerName",\n    DatabaseName = "UATDatabaseName",\n    UserID = "YourUsername",\n    Password = "YourPassword"\n};\n\nforeach (CrystalDecisions.CrystalReports.Engine.Table CrTable in reportDocument.Database.Tables)\n{\n    TableLogOnInfo tableLogonInfo = crTable.LogOnInfo;\n    tableLogonInfo.ConnectionInfo = crConnectionInfo;\n    tableLogonInfo.TableName = crTable.Name;\n    CrTable.ApplyLogOnInfo(tableLogonInfo);\n}\n\n// Now set the report source of your viewer\nCrystalReportViewer1.ReportSource = reportDocument;