using System;\nusing System.Windows.Forms;\n\npublic class MyForm : Form {\n    private TextBox textBoxName;\n    private Button buttonSubmit;\n    private ErrorProvider errorProvider1;\n\n    public MyForm() {\n        textBoxName = new TextBox();\n        buttonSubmit = new Button { Text = "Submit", Top = 30 };\n        errorProvider1 = new ErrorProvider();\n\n        this.Controls.Add(textBoxName);\n        this.Controls.Add(buttonSubmit);\n        buttonSubmit.Click += OnButtonSubmitClick;\n    }\n\n    private void OnButtonSubmitClick(object sender, EventArgs e) {\n        bool isValid = ValidateInputs();\n        if (isValid) {\n            MessageBox.Show("Form submitted successfully!", "Success", MessageBoxButtons.OK, MessageBoxIcon.Information);\n        }\n    }\n\n    private bool ValidateInputs() {\n        errorProvider1.Clear(); // Clear previous errors\n\n        if (string.IsNullOrWhiteSpace(textBoxName.Text)) {\n            errorProvider1.SetError(textBoxName, "Name is required.");\n            return false;\n        }\n\n        // Add more validation checks here as needed...\n\n        return true;\n    }\n}\n\nstatic class Program {\n    [STAThread]\n    static void Main() {\n        Application.EnableVisualStyles();\n        Application.Run(new MyForm());\n    }\n}