using System.Web;\nusing System.Web.Mvc;\nusing System.Web.Routing;\n\npublic class WikiRouteConstraint : IRouteConstraint\n{\n    public bool Match(HttpContextBase httpContext, Route route,\n                      string parameterName, RouteValueDictionary values,\n                      RouteDirection routeDirection)\n    {\n        // Get the requested path.\n        var path = (string)values["path"];\n\n        if (string.IsNullOrEmpty(path))\n            return false;\n\n        // Split the path into segments and check if the last segment is "wiki".\n        var segments = path.Split('/');\n        return segments.Length > 0 && segments[segments.Length - 1].Equals("wiki", StringComparison.OrdinalIgnoreCase);\n    }\n}