using System;\n\nclass Program\n{\n    static void Main()\n    {\n        var installedVersion = Environment.Version;\n        Console.WriteLine($"Installed .NET Framework Version: {installedVersion}");\n\n        // Check if the installed version is at least 3.5\n        if (installedVersion.Major >= 4)\n        {\n            Console.WriteLine(".NET Framework 3.5 or higher is installed.");\n        }\n        else\n        {\n            Console.WriteLine("Required .NET Framework version not found.");\n        }\n    }\n}