using System;\nusing System.Diagnostics;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    [DllImport("user32.dll")]\n    private static extern IntPtr FindWindow(string lpClassName, string lpWindowName);\n\n    [DllImport("user32.dll")]\n    private static extern bool SetForegroundWindow(IntPtr hWnd);\n\n    [DllImport("user32.dll")]\n    private static extern bool ShowWindowAsync(IntPtr hWnd, int nCmdShow);\n\n    // Constants\n    private const int SW_RESTORE = 9;\n\n    public static void Main()\n    {\n        string applicationTitle = "GoogleTalk"; // or the window title of the app you are targeting\n\n        // Find the window handle for the specified application window title\n        IntPtr hWnd = FindWindow(null, applicationTitle);\n\n        if (hWnd != IntPtr.Zero)\n        {\n            // Restore and bring to foreground the found window\n            ShowWindowAsync(hWnd, SW_RESTORE);\n            SetForegroundWindow(hWnd);\n        }\n        else\n        {\n            Console.WriteLine("The application is not running.");\n        }\n    }\n}