using System;\nusing System.IO;\nusing System.Xml;\nusing System.Xml.XPath;\n\nclass Program\n{\n    static void Main()\n    {\n        XPathDocument xmlDoc = new XPathDocument("fileIn.xml", XmlSpace.Preserve);\n        XPathNavigator xmlNav = xmlDoc.CreateNavigator();\n        XPathNodeIterator xmlNode = xmlNav.Select("/books/chapter[page[@process='True']]");\n\n        while (xmlNode.MoveNext())\n        {\n            XPathNavigator paraNode = xmlNode.Current.SelectSingleNode("para");\n            if (paraNode != null)\n            {\n                StringWriter stringWriter = new StringWriter();\n                XmlWriterSettings settings = new XmlWriterSettings { Indent = false };\n                using (XmlWriter writer = XmlWriter.Create(stringWriter, settings))\n                {\n                    paraNode.WriteTo(writer);\n                }\n                string content = stringWriter.ToString();\n                // Use 'content' as needed\n                Console.WriteLine(content);\n            }\n        }\n    }\n}