using System;\nusing System.Net.Http;\nusing System.Text;\nusing System.Threading.Tasks;\n\npublic class ReportRequester\n{\n    private readonly HttpClient _client;\n\n    public ReportRequester()\n    {\n        _client = new HttpClient();\n    }\n\n    public async Task<string> GetReportAsync(string username, string password, string reportEndpoint)\n    {\n        // Encoding the username and password for HTTP Basic Authentication\n        var credentials = Convert.ToBase64String(Encoding.UTF8.GetBytes($"{username}:{password}"));\n        _client.DefaultRequestHeaders.Add("Authorization", $"Basic {credentials}");\n\n        // Creating a content object to send via POST\n        HttpContent content = new StringContent("{your JSON data here}", Encoding.UTF8, "application/json");\n\n        // Sending the request and getting the response\n        HttpResponseMessage response = await _client.PostAsync(reportEndpoint, content);\n\n        if (response.IsSuccessStatusCode)\n        {\n            return await response.Content.ReadAsStringAsync();\n        }\n        else\n        {\n            throw new Exception($"Error: {response.StatusCode} - {await response.Content.ReadAsStringAsync()}");\n        }\n    }\n}\n\n// Example usage:\nclass Program\n{\n    static async Task Main(string[] args)\n    {\n        var requester = new ReportRequester();\n        try\n        {\n            string reportData = await requester.GetReportAsync("yourUsername", "yourPassword", "https://api.example.com/report");\n            Console.WriteLine(reportData);\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine(ex.Message);\n        }\n    }\n}