using System;\nusing System.Windows.Forms;\n\npublic class AutoScrollForm : Form\n{\n    private Panel panel;\n    private Label label;\n\n    public AutoScrollForm()\n    {\n        // Initialize the panel and set its properties\n        panel = new Panel\n        {\n            Parent = this,\n            Dock = DockStyle.Fill,\n            AutoScroll = true\n        };\n\n        // Initialize the label and set its properties\n        label = new Label\n        {\n            Parent = panel,\n            Text = "Your very long text that needs to be scrolled",\n            AutoSize = false,  // Ensure the label can resize\n            Width = panel.Width // Set width to panel's width initially\n        };\n\n        // Adjust label size based on text\n        label.SizeChanged += (sender, args) =>\n        {\n            panel.HorizontalScroll.Value = panel.HorizontalScroll.Maximum - panel.HorizontalScroll.SmallChange;\n        };\n    }\n\n    [STAThread]\n    static void Main()\n    {\n        Application.Run(new AutoScrollForm());\n    }\n}