using NCalc;\n\nclass Program\n{\n    static void Main()\n    {\n        Expression e = new Expression("(A && B) || (A && C) || (!B && C)");\n\n        var variables = new Dictionary<string, bool>\n        {\n            { "A", true },\n            { "B", false },\n            { "C", true }\n        };\n\n        foreach (var variable in variables)\n        {\n            e.Parameters[variable.Key] = variable.Value;\n        }\n\n        object result = e.Evaluate();\n        Console.WriteLine($"The result of the expression is: {result}");\n    }\n}