using System;\nusing System.Runtime.InteropServices;\n\npublic class MyObject\n{\n    public int IntegerProperty { get; set; }\n    public double DoubleProperty { get; set; }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        var obj = new MyObject();\n        \n        // This will not work for reference types directly\n        // It's used here to demonstrate, but note the limitations.\n        int size = Marshal.SizeOf(obj);\n        Console.WriteLine($"Approximate size of object: {size} bytes");\n    }\n}