using System;\n\nclass Program\n{\n    static void Main()\n    {\n        // Collect garbage to get a better baseline\n        GC.Collect();\n        GC.WaitForPendingFinalizers();\n        GC.Collect();\n\n        long memoryBefore = GC.GetTotalMemory(false);\n        \n        var obj = new MyObject { IntegerProperty = 1, DoubleProperty = 2.0 };\n        \n        long memoryAfter = GC.GetTotalMemory(false);\n        Console.WriteLine($"Estimated size of object: {memoryAfter - memoryBefore} bytes");\n    }\n}