#include <cstdlib>\n\nvoid RegisterTypeLibrary(const std::wstring& dllPath) {\n    std::wstring command = L"regasm.exe \"" + dllPath + L"\" /tlb:" + dllPath + L" /codebase";\n    int result = _wsystem(command.c_str());\n\n    if (result == 0) {\n        std::wcout << L"Type library registered successfully." << std::endl;\n    } else {\n        std::wcout << L"Failed to register type library." << std::endl;\n    }\n}