#include <windows.h>\n#include <objbase.h> // For CLSIDFromString\n\nGUID StringToGuid(LPWSTR guidString) {\n    GUID result;\n    HRESULT hr = CLSIDFromString(guidString, &result);\n    if (FAILED(hr)) {\n        // Handle error, e.g., log or throw exception\n        CoInitialize(NULL); // Ensure COM is initialized if not already\n        ZeroMemory(&result, sizeof(GUID));\n        // Consider throwing an exception or returning a default GUID\n    }\n    return result;\n}\n\n// Example usage:\nint main() {\n    LPWSTR guidString = L"{00000000-1111-2222-3333-444455556666}";\n    GUID guid = StringToGuid(guidString);\n    \n    // Do something with the GUID\n    return 0;\n}