#include <windows.h>\n#include <iostream>\n\nint main() {\n    DWORD sectorsPerCluster;\n    DWORD bytesPerSector;\n\n    if (GetDiskFreeSpace(\n        "C:\\",                // Drive to check; change this as necessary\n        &sectorsPerCluster,\n        &bytesPerSector,\n        nullptr,               // freeClusters\n        nullptr)) {            // totalClusters\n\n        std::cout << "Bytes per cluster: " << sectorsPerCluster * bytesPerSector << std::endl;\n    } else {\n        std::cerr << "Error getting disk information." << std::endl;\n    }\n\n    return 0;\n}