#include <iostream>\n#include <cstdlib>\n#include <string>\n\nint main() {\n    std::cout << "Content-type: text/html\r\n\r\n";\n    std::cout << "<html><body>";\n\n    // Get content length to know how much data is coming in\n    char* lenStr = getenv("CONTENT_LENGTH");\n    if (lenStr != nullptr) {\n        int contentLength = std::stoi(lenStr);\n        if (contentLength > 0) {\n            char* buffer = new char[contentLength + 1];\n            std::cin.read(buffer, contentLength);\n            buffer[contentLength] = '\0';\n\n            // Parse POST data\n            std::string postData(buffer);\n            size_t pos = 0;\n            while (pos < postData.length()) {\n                size_t nextPos = postData.find('&', pos);\n                std::string keyValue = postData.substr(pos, nextPos - pos);\n                size_t equalPos = keyValue.find('=');\n                if (equalPos != std::string::npos) {\n                    std::string key = keyValue.substr(0, equalPos);\n                    std::string value = keyValue.substr(equalPos + 1);\n                    std::cout << "Key: " << key << ", Value: " << value << "<br>";\n                }\n                if (nextPos == std::string::npos) break;\n                pos = nextPos + 1;\n            }\n\n            delete[] buffer;\n        } else {\n            std::cout << "No data submitted.";\n        }\n    } else {\n        std::cout << "Unable to determine content length.";\n    }\n\n    std::cout << "</body></html>";\n    return 0;\n}