#include <iostream>\n#include <cstdlib>\n\nint main() {\n    std::cout << "Content-type: text/html\r\n\r\n";\n    std::cout << "<html><body>";\n    \n    if getenv("QUERY_STRING") != nullptr) {\n        std::string query(GETenv("QUERY_STRING"));\n        // Parse query string and extract data\n        size_t pos = 0;\n        while (pos < query.length()) {\n            size_t nextPos = query.find('&', pos);\n            std::string keyValue = query.substr(pos, nextPos - pos);\n            size_t equalPos = keyValue.find('=');\n            if (equalPos != std::string::npos) {\n                std::string key = keyValue.substr(0, equalPos);\n                std::string value = keyValue.substr(equalPos + 1);\n                std::cout << "Key: " << key << ", Value: " << value << "<br>";\n            }\n            if (nextPos == std::string::npos) break;\n            pos = nextPos + 1;\n        }\n    } else {\n        std::cout << "No data submitted.";\n    }\n\n    std::cout << "</body></html>";\n    return 0;\n}