#include <windows.h>\n#include <iostream>\n\nbool IsProcessInForeground(DWORD processId) {\n    HWND hwnd = GetForegroundWindow();\n    if (hwnd == NULL) {\n        return false;\n    }\n\n    DWORD foregroundProcessId;\n    GetWindowThreadProcessId(hwnd, &foregroundProcessId);\n\n    return foregroundProcessId == processId;\n}\n\n// Example usage\nint main() {\n    DWORD pidOfInterest = 1234; // Replace with the actual PID you are interested in\n    if (IsProcessInForeground(pidOfInterest)) {\n        std::cout << "The process is in the foreground." << std::endl;\n    } else {\n        std::cout << "The process is not in the foreground." << std::endl;\n    }\n    return 0;\n}